package com.dudu.lizhen.service;

import org.apache.http.HttpResponse;
import org.apache.http.util.EntityUtils;

import java.util.HashMap;
import java.util.Map;

/**
 * 车牌号识别
 * Created by lizhen on 2017/8/14.
 */

public class PlateNumber {
    public static void main(String[] args) {
        String host = "http://ocrcp.market.alicloudapi.com";
        String path = "/rest/160601/ocr/ocr_vehicle_plate.json";
        String method = "POST";
        String appcode = "ee39e0c1b97749729cb3afb2c2fff1c6";
        Map<String, String> headers = new HashMap<String, String>();
        //最后在header中的格式(中间是英文空格)为Authorization:APPCODE 83359fd73fe94948385f570e3c139105
        headers.put("Authorization", "APPCODE " + appcode);
        //根据API的要求，定义相对应的Content-Type
        headers.put("Content-Type", "application/json; charset=UTF-8");
        Map<String, String> querys = new HashMap<String, String>();
        String bodys = "{\n" +
                "    \"inputs\": [\n" +
                "    {\n" +
                "        \"image\": {\n" +
                "            \"dataType\": 50,                      \n" +
                "            \"dataValue\": \"\"     \n" +
                "        },\n" +
                "        \"configure\": {\n" +
                "            \"dataType\": 50,\n" +
                "            \"dataValue\": \"{\\\"multi_crop\\\":false}\" \n" +
                "        }\n" +
                "    }]\n" +
                "}";


        try {
            /**
             * 重要提示如下:
             * HttpUtils请从
             * https://github.com/aliyun/api-gateway-demo-sign-java/blob/master/src/main/java/com/aliyun/api/gateway/demo/util/HttpUtils.java
             * 下载
             *
             * 相应的依赖请参照
             * https://github.com/aliyun/api-gateway-demo-sign-java/blob/master/pom.xml
             */
            HttpResponse response = HttpUtils.doPost(host, path, method, headers, querys, bodys);
            System.out.println("============"+ response.toString());
            //获取response的body
            System.out.println(EntityUtils.toString(response.getEntity()));
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
